import React, { useState } from "react";
import "./NouvelEtudiant.css";
function NouvelEtudiant(props) {
  const [nom, setNom] = useState("");
  const [prenom, setPrenom] = useState("");
  const [numeroAdmission, setNumeroAdmission] = useState("");

  function ajouterNouvelEtudiantHandler(event) {
    event.preventDefault();
    const nouvelEtudiant = {
      numero: numeroAdmission,
      nom: nom,
      prenom: prenom,
    };
    if (nom === "" || prenom === "" || numeroAdmission === "") {
      alert(
        "Les champs nom, prenom et numéro d'admission ne doivent pas être vides."
      );
      return;
    }
    let numeroTrouve = props.etudiants.filter(
      (unEtudiant) => unEtudiant.numero === numeroAdmission
    );
    if (numeroTrouve.length > 0) {
      alert("L'étudiant est déjà inscrit au cours.");
      setNumeroAdmission("");
      return;
    }
    props.ajouterEtudiant(nouvelEtudiant);
    setNumeroAdmission("");
    setNom("");
    setPrenom("");
  }
  function prenomHandler(event) {
    if (event.target.value.match(/\d/)) {
      event.preventDefault();
      return;
    }
    setPrenom(event.target.value);
  }
  function nomHandler(event) {
    if (event.target.value.match(/\d/)) {
      event.preventDefault();
      return;
    }
    setNom(event.target.value);
  }

  function numeroAdmissionHandler(event) {
    if (!event.target.value.match(/\d/)) {
      event.preventDefault();

      return;
    }
    setNumeroAdmission(event.target.value);
  }

  return (
    <form onSubmit={ajouterNouvelEtudiantHandler} className="formulaire">
      <h2>Inscription d'un étudiant</h2>
      <label for="prenom">Prénom : <input
        type="text"
        id="prenom"
        value={prenom}
        onChange={prenomHandler}
        placeholder="Prenom"
      ></input></label>
      <label for="nom">Nom : <input
        type="text"
        id="nom"
        value={nom}
        onChange={nomHandler}
        placeholder="Nom"
      ></input></label>
      <input
        type="text"
        value={numeroAdmission}
        onChange={numeroAdmissionHandler}
        placeholder="Numéro d'admission"
      ></input>
      <button type="submit">Ajouter un étudiant</button>
    </form>
  );
}

export default NouvelEtudiant;
