import React, { useState } from "react";
import ListeProfesseurs from "../components/ListeProfesseurs";
import NouveauProfesseur from "../components/NouveauProfesseur";
function Professeurs() {
  const image =
    "https://cdn.pixabay.com/photo/2016/11/19/13/08/business-1839191_960_720.jpg";
  const [professeurs, setProfesseurs] = useState([
    {
      embauche: new Date("2023-01-01").toDateString(),
      nom: "Labranche",
      prenom: "Sylvain",
      image: image,
      cours: ["Web et bases de données"]
    },
    {
      embauche: new Date("2022-09-01").toDateString(),
      nom: "Bergeron",
      prenom: "Mathieu",
      image: image,
      cours: [
        "Structures de données",
        "Environnement graphique client/serveur"
      ]
    },
    {
      embauche: new Date("2023-01-01").toDateString(),
      nom: "Bouchard",
      prenom: "Louis",
      image: image,
      cours: ["Analyse et conception de modèles"]
    },
    {
      embauche: new Date("2022-09-01").toDateString(),
      nom: "Deschênes",
      prenom: "Simon",
      image: image,
      cours: ["Interface utilisateur", "Objets connectés"]
    },
    {
      embauche: new Date("2022-09-01").toDateString(),
      nom: "Desmangles",
      prenom: "Nathalie",
      image: image,
      cours: ["Interface Web"]
    },
    {
      embauche: new Date("2022-01-01").toDateString(),
      nom: "Haouari",
      prenom: "Dorsaf",
      image: image,
      cours: ["Système d'exploitation Linux", "Sécurité et éthique"]
    },
    {
      embauche: new Date("2022-09-01").toDateString(),
      nom: "Fostiné",
      prenom: "Kerlyne",
      image: image,
      cours: ["Bases de données"]
    },
    {
      embauche: new Date("2021-09-01").toDateString(),
      nom: "Ait-Ammar",
      prenom: "Farida",
      image: image,
      cours: ["Algorithmie et programmation", "Programmation orientée objet"]
    },
    {
      embauche: new Date("2022-01-01").toDateString(),
      nom: "Lecomte",
      prenom: "Claude",
      image: image,
      cours: ["Programmation structurée"]
    },
    {
      embauche: new Date("2022-01-01").toDateString(),
      nom: "Mlouki",
      prenom: "Ons",
      image: image,
      cours: ["Environnements virtuels et réseau local"]
    },
    {
      embauche: new Date("2021-09-01").toDateString(),
      nom: "Boughanmi",
      prenom: "Ferdaws",
      image: image,
      cours: ["Système d'exploitation Windows"]
    },
    {
      embauche: new Date("2021-09-01").toDateString(),
      nom: "Bucci",
      prenom: "François",
      image: image,
      cours: ["Outils et profession"]
    },
  ]);

  function ajouterProfesseur(nouveauProf) {
    setProfesseurs(professeurs.concat(nouveauProf));
  }
  return (
    <div>
      <NouveauProfesseur image={image} ajouterProfesseur={ajouterProfesseur} />
      <ListeProfesseurs professeurs={professeurs} />
    </div>
  );
}

export default Professeurs;
