import React, { useState } from "react";
import "./NouveauProfesseur.css"
function NouveauProfesseur(props) {
  const [nom, setNom] = useState("");
  const [prenom, setPrenom] = useState("");
  const [dateEmbauche, setDateEmbauche] = useState(Date);

  function ajouterNouveauProfesseurHandler(event) {
    event.preventDefault();
    const nouveauProf = {
      embauche: dateEmbauche,
      nom: nom,
      prenom: prenom,
      image: props.image,
      cours: []
    }
    if(nom === "" || prenom === ""){
        alert("Les champs nom et prenom ne doivent pas être vides.")
        return;
    }
    props.ajouterProfesseur(nouveauProf)
    setNom("")
    setPrenom("")

  }
  function prenomHandler(event) {
    if (event.target.value.match(/\d/)) {
      event.preventDefault();
      return;
    }
    setPrenom(event.target.value);
  }
  function nomHandler(event) {
    if (event.target.value.match(/\d/)) {
      event.preventDefault();
      return;
    }
    setNom(event.target.value);
  }

  function dateEmbaucheHandler(event) {
    setDateEmbauche(event.target.value);
  }
  return (
    <form onSubmit={ajouterNouveauProfesseurHandler} className="formulaire">
        <h2>Embauche d'un professeur</h2>
      <label>Prénom : <input type="text" value={prenom} onChange={prenomHandler} placeholder="Prenom"></input></label>
      <input type="text" value={nom} onChange={nomHandler} placeholder="Nom"></input>
      <input type="date" value={dateEmbauche} onChange={dateEmbaucheHandler} ></input>
      <button type="submit">Ajouter un professeur</button>
    </form>
  );
}

export default NouveauProfesseur;
