import React, { useState } from "react";
import ListeCours from "../components/ListeCours";
import NouveauCours from "../components/NouveauCours";
import "../components/NouveauCours.css";
import donnees from "../../data/donnees"
function Cours() {
  const [sessionSelectionnee, setSessionSelectionnee] = useState("Session 4");
  
  const [cours, setCours] = useState(donnees.cours.filter((unCours) => unCours.dateDebut === "2023-01-01"));
  function ajouterCours(nouveauCours) {
    setCours(cours.concat(nouveauCours));
  }

  return (
    <div>
      <NouveauCours ajouterCours={ajouterCours} />
      <div className="formulaire"><h3>Sélectionnez une session à afficher</h3>
        <select
        value={sessionSelectionnee} 
        onChange={(event) => {
          setSessionSelectionnee(event.target.value);
          
          switch (event.target.value) {
            case "Session 1":
                
              setCours(
                donnees.cours.filter((unCours) => unCours.dateDebut === "2021-09-01")
              );
              break;
            case "Session 2":
              setCours(
                donnees.cours.filter((unCours) => unCours.dateDebut === "2022-01-01")
              );
              break;
            case "Session 3":
              setCours(
                donnees.cours.filter((unCours) => unCours.dateDebut === "2022-09-01")
              );
              break;
            case "Session 4":
              setCours(
                donnees.cours.filter((unCours) => unCours.dateDebut === "2023-01-01")
              );
              break;
            default:
              break;
          }
        }} 
      >
        <option value="Session 1">Session 1</option>
        <option value="Session 2">Session 2</option>
        <option value="Session 3">Session 3</option>
        <option value="Session 4">Session 4</option>
      </select></div>
      <ListeCours cours={cours} />
    </div>
  );
}

export default Cours;
