import React, { useState } from "react";
import AffichageCour from "../components/AffichageCour";
import { useParams } from "react-router-dom";

const maxEtudiants = 30;
const COURS = [
    {
        id: 1,
        titre: "Outils et profession",
        discipline: "informatique",
        maxEtudiants: 2,
        dateDebut: "2021-09-01",
        dateFin: "2021-12-31"
    },
    {
        id: 2,
        titre: "Système d'exploitation Windows",
        discipline: "informatique",
        maxEtudiants: maxEtudiants,
        dateDebut: "2021-09-01",
        dateFin: "2021-12-31"
    },
    {
        id: 3,
        titre: "Environnements virtuels et réseau local",
        discipline: "informatique",
        maxEtudiants: maxEtudiants,
        dateDebut: "2022-01-01",
        dateFin: "2021-06-06"
    },
    {
        id: 4,
        titre: "Programmation structurée",
        discipline: "informatique",
        maxEtudiants: maxEtudiants,
        dateDebut: "2022-01-01",
        dateFin: "2021-06-06"
    },
    {
        id: 5,
        titre: "Algorithmie et programmation",
        discipline: "informatique",
        maxEtudiants: maxEtudiants,
        dateDebut: "2021-09-01",
        dateFin: "2021-12-31"
    },
    {
        id: 6,
        titre: "Programmation orientée objet",
        discipline: "informatique",
        maxEtudiants: maxEtudiants,
        dateDebut: "2022-01-01",
        dateFin: "2021-06-06"
    },
    {
        id: 7,
        titre: "Bases de données",
        discipline: "informatique",
        maxEtudiants: maxEtudiants,
        dateDebut:"2022-09-01",
        dateFin: "2022-12-31"
    },
    {
        id: 8,
        titre: "Système d'exploitation Linux",
        discipline: "informatique",
        maxEtudiants: maxEtudiants,
        dateDebut:"2022-01-01",
        dateFin: "2022-06-06"
    },
    {
        id: 9,
        titre: "Sécurité et éthique",
        discipline: "informatique",
        maxEtudiants: maxEtudiants,
        dateDebut:"2022-09-01",
        dateFin: "2022-12-31"
    },
    {
        id: 10,
        titre: "Interface Web",
        discipline: "informatique",
        maxEtudiants: maxEtudiants,
        dateDebut:"2022-09-01",
        dateFin: "2022-12-31"
    },
    {
        id: 11,
        titre: "Interface utilisateur",
        discipline: "informatique",
        maxEtudiants: maxEtudiants,
        dateDebut: "2022-09-01",
        dateFin: "2022-12-31"
    },
    {
        id: 12,
        titre: "Objets connectés",
        discipline: "informatique",
        maxEtudiants: maxEtudiants,
        dateDebut:"2023-01-01",
        dateFin: "2023-06-06"
    },
    {
        id: 13,
        titre: "Analyse et conception de modèles",
        discipline: "informatique",
        maxEtudiants: maxEtudiants,
        dateDebut:"2023-01-01",
        dateFin: "2023-06-06"
    },
    {
        id: 14,
        titre: "Environnement graphique client/serveur",
        discipline: "informatique",
        maxEtudiants: maxEtudiants,
        dateDebut:"2023-01-01",
        dateFin: "2023-06-06"
    },
    {
        id: 15,
        titre: "Structures de données",
        discipline: "informatique",
        maxEtudiants: maxEtudiants,
        dateDebut:"2022-09-01",
        dateFin: "2022-12-31"
    },
    {
        id: 16,
        titre: "Web et bases de données",
        discipline: "informatique",
        maxEtudiants: maxEtudiants,
        dateDebut: "2023-01-01",
        dateFin: "2023-06-06"
    }
];

function Cour(){
    const courId = useParams().courId;
    const courCharge = COURS.at(courId-1);
    
    return (
    <div>
        <AffichageCour key={courCharge.id} element={courCharge} />
    </div>

    )

}

export default Cour;
