import React from "react";
import Card from "../../shared/Card";
import "./Cours.css";
function Cours({element}) {
  return (
    <li className="cours-item">
      <Card className="cours-item__content">
        <div className="cours-item__info">
          <h2>{element.titre}</h2>
          <h3>Discipline : {element.discipline}</h3>
          <h3>Nombre maximum d'étudiants : {element.nbEtudiantsMax}</h3>
          <h3>Date de début : {element.dateDebut}</h3>
          <h3>Date de fin : {element.dateFin}</h3>
        </div>
      </Card>
    </li>
  );
}

export default Cours;
