import React from "react";
import Avatar from "../../shared/Avatar";
import Card from "../../shared/Card";
import "./Professeur.css";
function Professeur({prof}) {
  return (
    <li className="professeur-item">
      <Card className="professeur-item__content">
        <div className="professeur-item__image">
          <Avatar image={prof.image} alt={prof.prenom} />
        </div>
        <div className="professeur-item__info">
          <h2>{prof.prenom} {prof.nom}</h2>
          <h3>Liste des cours:</h3>
          <ul>
          {prof.cours.length === 0 ? <p>Aucun cours pour le moment</p> : 
          prof.cours.map((cour, index) => (
            <li key={index}>{cour}</li>
          ))}
          </ul>
          
        </div>
      </Card>
    </li>
  );
}

export default Professeur;
