import React, { useState } from "react";
import "./NouveauCours.css";
import donnees from "../../data/donnees";
function NouveauCours(props) {
  const [titre, setTitre] = useState("");
  const [discipline, setDiscipline] = useState("");
  const [nbEtudiantsMax, setNbEtudiantsMax] = useState(0);
  const [dateDebut, setDateDebut] = useState(Date);
  const [dateFin, setDateFin] = useState(Date);
  const [professeurs, setProfesseurs] = useState(donnees.professeurs);
  const [profSelectionnee, setProfSelectionnee] = useState("");
  function ajouterNouveauCoursHandler(event) {
    event.preventDefault();
    const nouveauCours = {
      titre: titre,
      discipline: discipline,
      nbEtudiantsMax: nbEtudiantsMax,
      dateDebut: dateDebut,
      dateFin: dateFin,
      etudiants: [],
    };
    if (titre === "" || discipline === "" || nbEtudiantsMax === 0) {
      alert("Les champs ne peuvent pas être vides.");
      return;
    } else if (dateDebut > dateFin) {
      alert("La date de début doit être avant celle de fin");
      return;
    } else if (dateDebut < "2023-01-01" || dateFin > "2023-06-06") {
      alert(
        "Les dates doivent êtres cette année, entre le 1er janvier et le 6 juin inclusivement"
      );
      return;
    }
    for(let i = 0; i < professeurs.length; i++){
      if ((professeurs[i].prenom + " " + professeurs[i].nom) === profSelectionnee){
        professeurs[i].cours.push(nouveauCours.titre);
        setProfesseurs(professeurs);
      }
    }
    props.ajouterCours(nouveauCours);
    setProfSelectionnee("");
    setTitre("");
    setDiscipline("");
    setNbEtudiantsMax(0);
    setDateDebut("");
    setDateFin("");
  }
  function titreHandler(event) {
    if (event.target.value.match(/\d/)) {
      event.preventDefault();
      return;
    }
    setTitre(event.target.value);
  }
  function disciplineHandler(event) {
    if (event.target.value.match(/\d/)) {
      event.preventDefault();
      return;
    }
    setDiscipline(event.target.value);
  }
  function nbEtudiantsMaxHandler(event) {
    setNbEtudiantsMax(event.target.value);
  }
  function dateDebutHandler(event) {
    setDateDebut(event.target.value);
  }
  function dateFinHandler(event) {
    setDateFin(event.target.value);
  }
  return (
    <form onSubmit={ajouterNouveauCoursHandler} className="formulaire">
      <h2>Ajout d'un cours</h2>
      <label for="titre">Titre : <input
        type="text"
        id="titre"
        value={titre}
        onChange={titreHandler}
        placeholder="Titre"
      ></input></label>
      
      <label for="Discipline">Discipline : <input
        type="text"
        id="Discipline"
        value={discipline}
        onChange={disciplineHandler}
        placeholder="Discipline"
      ></input></label>
      <label for="nbEtudiantsMax">Nombre maximal d'Étudiants : <input
        type="number"
        id="nbEtudiantsMax"
        value={nbEtudiantsMax}
        onChange={nbEtudiantsMaxHandler}
        placeholder="Nombre maximal d'étudiants"
      ></input></label>
      <input
        type="date"
        value={dateDebut}
        onChange={dateDebutHandler}
        placeholder="Date de début"
      ></input>
      <input
        type="date"
        value={dateFin}
        onChange={dateFinHandler}
        placeholder="Date de fin"
      ></input>
      Sélectionnez un professeur (Le professeur ne se trouve pas dans la liste? Veuillez l'embaucher sur la page professeurs)<select
        value={profSelectionnee}
        onChange={(event) => {
          setProfSelectionnee(event.target.value);
        }}
        
      >{
        professeurs.map( (prof,index) => 
          <option key={index}>{prof.prenom} {prof.nom}</option> )
      }</select>
      <button type="submit">Ajouter un cours</button>
    </form>
  );
}

export default NouveauCours;
