import React, { useState } from "react";
import AffichageCour from "../components/AffichageCour";
import { useParams } from "react-router-dom";
import NouvelEtudiant from "../components/NouvelEtudiant";
import ListeEtudiants from "../components/ListeEtudiants";
import donnees from "../../data/donnees"


function Cour() {
  const [etudiants, setEtudiants] = useState([
    {
      numero: "1406084",
      prenom: "Mathieu",
      nom: "Girard",
    },
    {
      numero: "2008442",
      prenom: "Zachary",
      nom: "Labelle",
    },
  ]);
  const coursId = useParams().coursId;
  const coursChargee = donnees.cours.filter((unCours) => unCours.id === coursId);

  function ajouterEtudiant(nouvelEtudiant) {
    setEtudiants(etudiants.concat(nouvelEtudiant));
  }
  return (
    <div>
        
      <AffichageCour element={coursChargee[0]} />
      <NouvelEtudiant etudiants={etudiants} ajouterEtudiant={ajouterEtudiant} />
      <ListeEtudiants etudiants={etudiants}/>
    </div>
  );
}

export default Cour;
