import React from "react";

function AffichageCour({element}) {
    return (
          <div className="cours-item__info">
            <h2>{element.titre}</h2>
            <h3>Discipline : {element.discipline}</h3>
            <h3>Nombre maximum d'étudiants : {element.nbEtudiantsMax}</h3>
            <h3>Date de début : {element.dateDebut}</h3>
            <h3>Date de fin : {element.dateFin}</h3>
          </div>
    );
  }
  
  export default AffichageCour;
