import {
  BrowserRouter as Router,
  Route,
  Redirect,
  Switch,
} from "react-router-dom";
import React from "react";
import "./App.css";
import Professeurs from "./professeur/pages/Professeurs";
import Cours from "./cours/pages/Cours";
import Cour from "./unCours/pages/UnCours";
import MainNavigation from "./shared/Navigation/MainNavigation";
import MainFooter from "./shared/MainFooter"
import Accueil from "./Accueil.js"
function App() {
  return (
    <Router>
      <MainNavigation />
      <main>
      <Switch>
        <Route path="/" exact>
          <Accueil />
        </Route>
        <Route path="/professeurs" exact>
          <Professeurs />
        </Route>
        <Route path="/cours" exact>
          <Cours />
        </Route>
        <Route path="/:coursId/cours" exact>
          <Cour />
        </Route>

        <Redirect to="/" />
      </Switch>
      </main>
      <MainFooter>
        
      </MainFooter>
    </Router>
  );
}

export default App;
